import React, { useState } from "react";
import loginbg from "../assets/loginbg.png";
import { useNavigate, Link } from "react-router-dom";

export default function Login() {
  const navigate = useNavigate();
  const [sid, setSid] = useState("");
  const [username, setUsername] = useState("");
  const [rememberMe, setRememberMe] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    if (sid === "yourSID" && username === "yourUsername") {
      if (rememberMe) {
        // Save login state to local storage or cookies if needed
      }
      navigate("/Dashboard");
    } else {
      console.log("Invalid SID or username");
    }
  };

  const handleSidChange = (e) => {
    setSid(e.target.value);
  };

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handleRememberMeChange = (e) => {
    setRememberMe(e.target.checked);
  };

  return (
    <div className="relative w-full h-screen bg-zinc-900/90">
      <img
        className="absolute w-full h-full object-cover mix-blend-overlay"
        src={loginbg}
        alt="/"
      />

      <div className="flex justify-center items-center h-full">
        <form
          className="max-w-[400px] w-full mx-auto bg-white p-8"
          onSubmit={handleLogin}
        >
          <h2 className="text-4xl font-bold text-center py-4">EyePark</h2>

          <div className="flex flex-col mb-4">
            <label>SID</label>
            <input
              className="border relative bg-gray-100 p-2"
              type="text"
              value={sid}
              onChange={handleSidChange}
            />
          </div>
          <div className="flex flex-col ">
            <label>Username </label>
            <input
              className="border relative bg-gray-100 p-2"
              type="text"
              value={username}
              onChange={handleUsernameChange}
            />
          </div>
          <div className="mt-[20px] ml-[40px]">
            <Link to="/Dashboard">
              <button className="bg-[#31C97A] border-none text-black w-[500px] h-[40px] hover:bg-black hover:text-[#31C97A] rounded-xl">
                Submit
              </button>
            </Link>
          </div>
          <p className="flex items-center mt-2">
            <input
              className="mr-2"
              type="checkbox"
              checked={rememberMe}
              onChange={handleRememberMeChange}
            />
            Remember Me
          </p>
        </form>
      </div>
    </div>

  );
}
