import React from "react";
import loginbg from "../assets/loginbg.png";
import { useHistory } from "react-router-dom";

export default function Login() {
    const history =useHistory();
    const [sid, setSid] = useState("");
    const [username, setUsername] = useState("");
    const [rememberMe, setRememberMe] = useState(false);

    const handleLogin = (e) => {
      e.preventDefault();

      // Perform login logic
      if (sid === "yourSID" && username === "yourUsername") {
        // Successful login
        if (rememberMe) {
          // Save login state to local storage or cookies if needed
        }

        // Redirect to Dashboard after successful login
        history.push("/Dashboard");
      } else {
        // Failed login
        console.log("Invalid SID or username");
      }
    };

    const handleSidChange = (e) => {
      setSid(e.target.value);
    };

    const handleUsernameChange = (e) => {
      setUsername(e.target.value);
    };

    const handleRememberMeChange = (e) => {
      setRememberMe(e.target.checked);
    };

  return (
    <div className="relative w-full h-screen bg-zinc-900/90">
      <img
        className="absolute w-full h-full object-cover mix-blend-overlay"
        src={loginbg}
        alt="/"
      />

      <div className="flex justify-center items-center h-full">
        <form className="max-w-[400px] w-full mx-auto bg-white p-8">
          <h2 className="text-4xl font-bold text-center py-4">EyePark</h2>

          <div className="flex flex-col mb-4">
            <label>SID</label>
            <input className="border relative bg-gray-100 p-2" type="text" />
          </div>
          <div className="flex flex-col ">
            <label>Username</label>
            <input className="border relative bg-gray-100 p-2" type="text" />
          </div>
          <button className="w-full py-3 mt-8 bg-indigo-600 hover:bg-indigo-500 relative text-white">
            Sign In
          </button>
          <p className="flex items-center mt-2">
            <input className="mr-2" type="checkbox" />
            Remember Me
          </p>
        </form>
      </div>
    </div>
  );
}
